import React from 'react';
import { Button, Card, Container, Toast } from 'react-bootstrap';
import Navbar from 'react-bootstrap/esm/Navbar';
import { Link } from 'react-router-dom';
import { ProductList } from '../../../domain/dto/backendDos';
import BackendExtService from '../../../extService/BackendExtService';
import "./style.css";

type Props = {};
type State = {
    productList?: ProductList
    isShowToast: boolean
};

export default class ProductListingPage extends React.Component<Props, State> {
    state = {} as State;

    constructor(props: Props) {
        super(props);

        this.onLoadedProductList = this.onLoadedProductList.bind(this);
    }

    componentDidMount() {
        BackendExtService.getProductList(this.onLoadedProductList)

    }

    onLoadedProductList(data: ProductList) {
        this.setState({ productList: data });
    }

    renderProductItems() {
        const cards: JSX.Element[] = [];
        if (!this.state.productList) {
            return null;
        }
        for (var item of this.state.productList) {
            cards.push(
                <Link to={"/detail/" + item.id}>
                    <Card className="productCard">
                        <Card.Img className="image centerCropped" variant="top" src={item.imageUrl} />
                        <Card.Body>
                            <Card.Title>{item.name}</Card.Title>
                            <Card.Text>
                                ${item.price}
                            </Card.Text>
                        </Card.Body>
                    </Card>
                </Link>
            )
        }
        return cards;
    }

    

    render() {
        return (
            <div>

                <Container>
                    <div className="productContainer">
                        
                        {
                            (this.renderProductItems()) ? this.renderProductItems() : (
                                <div>Loading</div>
                            )
                        }
                    </div>
                </Container>
            </div>
        )
    }
}
