import React from 'react'
import { Button, Container, Form } from 'react-bootstrap';
import AuthService from '../../../service/AuthService';
import GoogleButton from 'react-google-button';
import { FacebookLoginButton } from 'react-social-login-buttons';
import "./style.css";

type Props = {
    authService: AuthService
};

type State = {
    email: string,
    password: string
};
//declaration
export default class LoginPage extends React.Component<Props, State> {
    //Initial State
    state = {
        email: "",
        password: ""
    } as State;

    constructor(props: Props) {
        super(props);

        this.handleInputChange=this.handleInputChange.bind(this);
        this.onClickSignInButton=this.onClickSignInButton.bind(this);
        this.onSignInWithGoogle=this.onSignInWithGoogle.bind(this);
        this.onSignInWithFacebook=this.onSignInWithFacebook.bind(this);
    }

    handleInputChange(event: React.ChangeEvent<HTMLInputElement>) {
        const target = event.target;
        const value = target.value;
        const name = target.name;

        // @ts-ignore
        this.setState({
            [name]: value
        });
    }

    onClickSignInButton(event: React.FormEvent<HTMLFormElement>){
        event.preventDefault();//prevent change page
        
        this.props.authService.signInWithEmailPassword(this.state.email,this.state.password,this.onSignInError);
    }

    onSignInWithGoogle(event: React.MouseEvent<HTMLDivElement>){
        event.preventDefault();

        this.props.authService.signInWithGoogle(this.onSignInError);
    }

    onSignInWithFacebook(){
        this.props.authService.signInWithFacebook(this.onSignInError);
    }
    
    onSignInError(code: string, message: string){
        alert(code + ": " + message)
    }

    render() {
        return (
            <div className="loginpanel">
                <Container>
                <Form onSubmit={this.onClickSignInButton}>
                    <Form.Group controlId="formBasicEmail">
                        <Form.Label>Email address</Form.Label>
                        <Form.Control name="email" type="email" placeholder="Enter email" 
                        value={this.state.email} onChange={this.handleInputChange} />
                        <Form.Text className="text-muted">
                            We'll never share your email with anyone else.
                        </Form.Text>
                    </Form.Group>

                    <Form.Group controlId="formBasicPassword">
                        <Form.Label>Password</Form.Label>
                        <Form.Control name="password" type="password" placeholder="Password" 
                        value={this.state.password} onChange={this.handleInputChange} />
                    </Form.Group>
                    <Form.Group controlId="formBasicCheckbox">
                        <Form.Check type="checkbox" label="Check me out" />
                    </Form.Group>
                    <Button variant="primary" type="submit" >
                        Login
                    </Button>
                </Form>
                <br/>
                <div className="socialButtonContainer">
                    <GoogleButton type="dark" onClick={this.onSignInWithGoogle}/>
                    <FacebookLoginButton 
                        onClick={this.onSignInWithFacebook}
                        className="facebookButton"
                        />
                </div>
                
                </Container>
            </div>
            
        )
    }
}
