import React from 'react'
import { Button, Carousel, Container } from 'react-bootstrap';


type Props = {};
type State = {};

export default class HomePage extends React.Component<Props, State> {

    state = {} as State;

    constructor(props: Props) {
        super(props);
    }

    onClickToProductList(){
        window.location.href='#/productlist'
    }
    onClickToWeather(){
        window.location.href='#/weather'
    }
    onClickToBus(){
        window.location.href='#/bus'
    }

    render() {
        return (
            <div className="homepage">
                <Carousel>
                    <Carousel.Item>
                        <img
                            className="d-block w-100"
                            src="../../../image/alps.png"
                            alt="First slide"
                        />
                        <Carousel.Caption>
                            <h3>Your journey starts here</h3>
                            <Button variant="primary" onClick={this.onClickToProductList}>Shop Now</Button>
                        </Carousel.Caption>
                    </Carousel.Item>
                    <Carousel.Item>
                        <img
                            className="d-block w-100"
                            src="../../../image/runway.png"
                            alt="Second slide"
                        />

                        <Carousel.Caption>
                            <h3>What's the weather like?</h3>
                            <p>Lorem ipsum dolor sit amet, consectetur adipiscing elit.</p>
                            <Button variant="primary" onClick={this.onClickToWeather}>Check here</Button>
                        </Carousel.Caption>
                    </Carousel.Item>
                    <Carousel.Item>
                        <img
                            className="d-block w-100"
                            src="holder.js/800x400?text=Third slide&bg=20232a"
                            alt="Third slide"
                        />

                        <Carousel.Caption>
                            <h3>Visit us?</h3>
                            <Button variant="primary" onClick={this.onClickToBus}>Find us</Button>
                        </Carousel.Caption>
                    </Carousel.Item>
                </Carousel>
            </div>
        )
    }
}
