import React from "react";
import { Breadcrumb, Container, Form, Toast } from "react-bootstrap";
import Button from "react-bootstrap/esm/Button";
import { RouteComponentProps, withRouter } from "react-router-dom";
import { ProductItem } from "../../../../domain/dto/backendDos";
import { ShoppingCartItem } from "../../../../domain/shoppingCartDos";
import BackendExtService from "../../../../extService/BackendExtService";
import ShoppingCartService from "../../../../service/ShoppingCartService";
import "./style.css";

type RouterParams = {
    productId: string
};

type Props = RouteComponentProps<RouterParams> & {
    shoppingCartService: ShoppingCartService
};
type State = {
    productDetail?: ProductItem
    productQuantity: ShoppingCartItem
    isShowToast: boolean
};

class ProductDetailPage extends React.Component<Props, State> {
    state = {
        isShowToast: false
    } as State;

    constructor(props: Props) {
        super(props);
        this.state={value:1};
        this.onClickAddToCartButton = this.onClickAddToCartButton.bind(this);
        this.onLoadedProductDetail = this.onLoadedProductDetail.bind(this);
        this.onCloseToast = this.onCloseToast.bind(this);
        this.onClickAddQuantity=this.onClickAddQuantity.bind(this);
        this.onClickMinusQuantity=this.onClickMinusQuantity.bind(this);
    }
    componentDidMount() {
        BackendExtService.getProductDetail(+this.props.match.params.productId, this.onLoadedProductDetail);    
    }

    onLoadedProductDetail(data: ProductItem) {
        this.setState({ productDetail: data });
    }

    onClickAddToCartButton() {
        const shoppingCartService = this.props.shoppingCartService;
        const productDetail = this.state.productDetail!;
        productQuantity = this.state.productQuantity;       
        shoppingCartService.addToCart(productDetail.id,productQuantity);
        this.setState({
            isShowToast: true
        });
    }
    onClickAddQuantity(){
        this.setState(prevState => {value: ++prevState.value});
    }
    onClickMinusQuantity(){
        this.setState(prevState => {value: prevState.value > 0? --prevState.value : 0});
    }


    renderProductDetail() {
        const productDetail = this.state.productDetail!;

        return (
            <div>
                <Breadcrumb>
                    <Breadcrumb.Item href="#/">All Products</Breadcrumb.Item>
                    <Breadcrumb.Item active>{productDetail.name}</Breadcrumb.Item>
                </Breadcrumb>
                <h1>{productDetail.name}</h1>
                <img className="bannerImage" src={productDetail.imageUrl} alt={"Product " + productDetail.name + " Image"} />

                <h3>Description: </h3>
                <p>{productDetail.description}</p>

                <h4>Price: ${productDetail.price}</h4>
                
                <Button onClick={this.onClickMinusQuantity}>-</Button>
                <input type="text" value={this.state.value}/>
                <Button onClick={this.onClickAddQuantity}>+</Button>
                <Button
                    variant="primary"
                    onClick={this.onClickAddToCartButton}
                >
                    Add to cart
                </Button>
            </div>
        )
    }

    onCloseToast() {
        this.setState({
            isShowToast: false
        });
    }

    render() {
        return (
            <Container id="productDetailPage">
                <div className="toastContainer">
                    <Toast show={this.state.isShowToast} onClose={this.onCloseToast} delay={3000} autohide>
                        <Toast.Header>
                            <strong className="mr-auto">Added</strong>
                            <small>Click to close</small>
                        </Toast.Header>
                        <Toast.Body>It has been added to the cart. You can continue shopping</Toast.Body>
                    </Toast>
                </div>
                {
                    (this.state.productDetail) ? this.renderProductDetail() : (
                        <div>Loading...</div>
                    )
                }
            </Container>
        );
    }
}
export default withRouter(ProductDetailPage);
