import React from "react";
import { DisplayMap, ProductItem, ProductList, ProductMap, Transaction } from "../domain/dto/backendDos";
import { CheckoutResponseDto, ProductDetailResponseDto, ProductListResponseDto, ShoppingCartItemsResponseDto, TransactionResponseDto } from "../domain/dto/backendDtos";
import mockProductList from './products.json';
import mockProductDetail from './productDetail.json';
import mockinShoppingCartItems from './shoppingCartItems.json';
import mockCheckout from './checkout.json';
import { ShoppingCartItem } from "../domain/shoppingCartDos";
import axios from "axios";
import config from "../config/config";

export default class BackendExtService{

    static getProductList(callback: (data: ProductList) => void){
       axios.get<ProductListResponseDto>(config().backend.baseUrl + "/public/product/all")
       .then(response =>{
            callback(response.data as ProductList);
        })
    }
    static getProductDetail(productId: number, callback: (data: ProductItem) => void) {
      axios.get<ProductDetailResponseDto>(config().backend.baseUrl + "/public/product/details?productId="+productId)
      .then(response => {
            callback(response.data as ProductItem);
        })
    }
    static getShoppingCartItems(productIds: string[], callback: (data: ProductMap)=>void){
        axios.post<ProductDetailResponseDto>(config().backend.baseUrl + "/public/product/search/ids", productIds)
            .then(response => {
                callback(response.data as ProductMap);

            // when one day DO is finally mismatch with the DTO:
            // callback({
            //     productName: data.name,
            //     productPrice: data.price,
            //     productDescription: data.description,
            //     ...
            // })
        })
    }
    static checkout(idToken:string,items: ShoppingCartItem[], callback: (data: Transaction) => void){
        axios.post<CheckoutResponseDto>(config().backend.baseUrl + "/transaction", items,{
            headers: {
                Authorization:"Bearer " + idToken
            }
        })
        .then(response => { 
            callback(response.data as Transaction);
        })
    }
    static getTransaction(idToken:string, transactionId: number, callback: (data: Transaction) => void){
        axios.get<TransactionResponseDto>(config().backend.baseUrl + "/transaction/"+ transactionId,{
            headers: {
                Authorization:"Bearer " + idToken
            }
        })
        .then(response => {
            callback(response.data as Transaction);
        })


