import firebase from 'firebase';
import React from 'react'

type OnAuthStateChange = (isLoading: boolean)=>void;

export default class AuthService{
    firebaseConfig = {
        apiKey: "AIzaSyA8QwQuc5kHcPXv2CSwjph2C_pznIAZZ0Y",
        authDomain: "rafe-venturenix-project.firebaseapp.com",
        projectId: "rafe-venturenix-project",
        storageBucket: "rafe-venturenix-project.appspot.com",
        messagingSenderId: "214419668077",
        appId: "1:214419668077:web:be42c5b042ed852cff9364",
        measurementId: "G-1HF0PJ0NTK"
      };

      onAuthStateChange: OnAuthStateChange;

      constructor(onAuthStateChange: OnAuthStateChange){
        this.onAuthStateChange = onAuthStateChange;
      }

      init(){
        firebase.initializeApp(this.firebaseConfig);
      }
      signInWithEmailPassword(email: string, password: string){
        this.onAuthStateChange(true);
        firebase.auth().signInWithEmailAndPassword(email,password)
            .then((userCredential: firebase.auth.UserCredential) => {
                let user = userCredential.user;
                console.log("Email: ", user?.email);
                this.onAuthStateChange(false);
            })
            .catch((error)=>{
              console.log(error);
              this.onAuthStateChange(false);
            });
      }
}
