import React from "react";
import { ProductItem, ProductList, ProductMap, Transaction } from "../domain/dto/backendDos";
import { CheckoutResponseDto, ProductDetailResponseDto, ProductListResponseDto, ShoppingCartItemsResponseDto } from "../domain/dto/backendDtos";
import mockProductList from './products.json';
import mockProductDetail from './productDetail.json';
import mockinShoppingCartItems from './shoppingCartItems.json';
import mockCheckout from './checkout.json';
import { ShoppingCartItem } from "../domain/shoppingCartDos";
import axios from "axios";

export default class BackendExtService{
    static getProductList(callback: (data: ProductList) => void){
       axios.get<ProductListResponseDto>("http://localhost:8080/product/all")
       .then(response =>{
            callback(response.data as ProductList);
        })
    }
    static getProductDetail(productId: number, callback: (data: ProductItem) => void) {
      axios.get<ProductDetailResponseDto>("http://localhost:8080/product/details?productId="+productId)
      .then(response => {
            callback(response.data as ProductItem);
        })
    }
    static getShoppingCartItems(productIds: string[], callback: (data: ProductMap)=>void){
        axios.post<ShoppingCartItemsResponseDto>("http://localhost:8080/product/search/ids", productIds)
        .then(response => {
            callback(response.data as ProductMap);

            // when one day DO is finally mismatch with the DTO:
            // callback({
            //     productName: data.name,
            //     productPrice: data.price,
            //     productDescription: data.description,
            //     ...
            // })
        })
    }
    static checkout(items: ShoppingCartItem[], callback: (data: Transaction) => void){
        axios.post<CheckoutResponseDto>("http://localhost:8080/transaction", items)
        .then(response => {
            callback(response.data as Transaction);
        })
    }

    static getTransaction(transactionId: number, callback: (data: Transaction) => void){
        new Promise((resolve, reject)=>{
            setTimeout(()=>{
                resolve(mockCheckout as CheckoutResponseDto);
            },1000);
        }).then(data => {
            callback(data as Transaction);
        })
    }

    //postPayment API
}
