import { faShoppingCart, faSnowboarding, faSignOutAlt, faUser } from '@fortawesome/free-solid-svg-icons';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import React from 'react';
import { Container, Nav, Spinner } from 'react-bootstrap';
import Navbar from 'react-bootstrap/esm/Navbar';
import { HashRouter, Redirect, Route, Switch } from 'react-router-dom';
import './App.css';
import AuthService from './service/AuthService';
import ShoppingCartService from './service/ShoppingCartService';
import CheckoutPage from './ui/page/CheckoutPage';
import LoginPage from './ui/page/LoginPage';
import ProductListingPage from './ui/page/ProductListingPage';
import ProductDetailPage from './ui/page/ProductDetailPage';
import ShoppingCartPage from './ui/page/ShoppingCartPage';
import snowboardLogo from './image/snowboardLogo.png';
import HomePage from './ui/page/HomePage';
import Thankyoupage from './ui/page/ThankyouPage';
import { MDBCol, MDBContainer, MDBFooter, MDBRow } from 'mdbreact';
import WeatherPage from './ui/page/WeatherPage';


type Props = {};
type State = {
    isLoading: boolean
};

export default class App extends React.Component<Props, State> {
    state = {
        isLoading: false
    } as State;

  shoppingCartService: ShoppingCartService;
  authService: AuthService;

  constructor(props: Props) {
    super(props);
    this.onAuthStateChange = this.onAuthStateChange.bind(this);
    this.onClickSignOut = this.onClickSignOut.bind(this);

    this.shoppingCartService = new ShoppingCartService();
    this.authService = new AuthService(this.onAuthStateChange);

    

  }

  onAuthStateChange(isLoading: boolean){
    this.setState({
        "isLoading": isLoading
    })
  }

  onClickSignOut(event: React.MouseEvent<any>){
      event.preventDefault();
      this.authService.signOut();
  }

  componentDidMount() {
    this.authService.init();
  }

  render() {
      let loadingOverlayClassName = "loadingOverlay";
      if(this.state.isLoading){
          loadingOverlayClassName += " active";
      }
    return (
      <div className="App">
        <Navbar bg="light" expand="lg">
          <Container><Navbar.Brand href= "/productlist" ><img className='Navbarlogo'src={snowboardLogo}/> Ventail</Navbar.Brand>
            <Navbar.Collapse className="justify-content-end"></Navbar.Collapse>
            <Nav.Link href="#/cart"><FontAwesomeIcon icon={faShoppingCart} /></Nav.Link>
            {
                (AuthService.isSignedIn() ? 
                    (<Nav.Link onClick={this.onClickSignOut}>
                        <FontAwesomeIcon icon={faSignOutAlt} />
                    </Nav.Link>
                    ) : (
                    <Nav.Link href="#/login">
                        <FontAwesomeIcon icon={faUser} />
                    </Nav.Link>)
                )
            }
            
          </Container>
        </Navbar>
          {/* for seo purpose /detail/:productName/:productId */}
          <HashRouter>
            <Switch>
            {/* <Route exact path="/">
              <Redirect to="/home"/>
            </Route> */}
            <Route exact path="/home">
                <HomePage/>
              </Route>
              <Route exact path="/productlist">
                <ProductListingPage />
              </Route>
              <Route exact path="/login">
                <LoginPage
                  authService={this.authService}/>
              </Route>
              <Route exact path="/detail/:productId">
                <ProductDetailPage
                  shoppingCartService={this.shoppingCartService}
                />
                {/* /* new a ProductDetailPage then run constructor(newProductDetailPage({shoppingCartService: this.shoppingCarstService})), pack it as prop and pass back into this function*/}
              </Route>
              <Route exact path="/cart">
                <ShoppingCartPage 
                shoppingCartService={this.shoppingCartService}
                />
              </Route>
              <Route exact path="/checkout/:transactionId">
                <CheckoutPage/>
              </Route>
              <Route exact path="/weather">
                <WeatherPage/>
              </Route>
              <Route exact path="/thankyoupage">
                <Thankyoupage/>
              </Route>
            </Switch>
          </HashRouter>
          <Container>
            <MDBFooter color="blue" className="font-small pt-4 mt-4">
              <MDBContainer fluid className="text-center text-md-left">
                <MDBRow>
                  <MDBCol md="6">
                    <h5 className="title">Ventail Snowboard Shop</h5>
                    <p>
                      Ventail Snowboard Shop provides a wide range of equipments in one go.
                    </p>
                  </MDBCol>
                  <MDBCol md="6">
                    <h5 className="title">Links</h5>
                    <ul>
                      <li className="list-unstyled">
                        <a href="/productlist">Product List</a>
                      </li>
                      <li className="list-unstyled">
                        <a href="/weather">Weather</a>
                      </li>
                      <li className="list-unstyled">
                        <a href="#!">Location</a>
                      </li>
                      <li className="list-unstyled">
                        <a href="#!">About us</a>
                      </li>
                    </ul>
                  </MDBCol>
                </MDBRow>
              </MDBContainer>
              <div className="footer-copyright text-center py-3">
                <MDBContainer fluid>
                  &copy; {new Date().getFullYear()} Copyright: <a href="https://www.mdbootstrap.com"> MDBootstrap.com </a>
                </MDBContainer>
              </div>
            </MDBFooter>
            </Container>
      </div>
    );
  }
}
