import { faTrashAlt } from '@fortawesome/free-solid-svg-icons';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import React from 'react';
import { Breadcrumb, Button, Container, Table } from 'react-bootstrap';
import { ProductMap } from '../../../domain/dto/backendDos';
import BackendExtService from '../../../extService/BackendExtService';
import ShoppingCartSErvice from '../../../service/ShoppingCartService';
import './style.css';

type Props = {
    shoppingCartService: ShoppingCartSErvice
};
type State = {
    shoppingCartItems?: ProductMap
};

export default class ShoppingCartPage extends React.Component<Props, State> {
    state = {} as State; //initial state
    constructor(props: Props){
        super(props);

        this.onLoadedShoppingCartItems = this.onLoadedShoppingCartItems.bind(this);
        this.onClickRemoveItemButton = this.onClickRemoveItemButton.bind(this);
    }
    componentDidMount(){
        const cart = this.props.shoppingCartService.getCart();
        const productIds = Object.keys(cart); /*find out all the keys in the object*/
        BackendExtService.getShoppingCartItems(productIds, this.onLoadedShoppingCartItems)
    }

    onLoadedShoppingCartItems(data: ProductMap){
        this.setState({
            shoppingCartItems: data
        })
    }
    renderShoppingCartItems(){
        const cartItems: JSX.Element[]=[];

        if(!this.state.shoppingCartItems){
            return null;
        }

        for (let productId of Object.keys(this.state.shoppingCartItems)){
            //get the actual shopping cart item from this.state.shoppingCartItems by the key "productId"
            //Prefixed a "+" to productId to convert it from string to number
            const item = this.state.shoppingCartItems[+productId];
            cartItems.push(
                <tr>
                <td><img className="cartImg" src={item.imageUrl}/></td>
                <td>{item.name}</td>
                <td>{item.price}</td>
                <td>
                    <Button variant="danger" 
                    onClick={() => this.onClickRemoveItemButton(item.id)}
                >
                    <FontAwesomeIcon icon={faTrashAlt}/>Delete</Button></td>
            </tr>
            )
        }
        return cartItems
    };

    onClickRemoveItemButton(productId: number){
        // ... this state.shoppingCartItems means take everything
        //inside this.state.shoppingCartItems out and put them into the new object
        const shoppingCartItems = {...this.state.shoppingCartItems};
        
        delete shoppingCartItems[productId];

        this.setState({
            shoppingCartItems: shoppingCartItems
        });
    }
    
    render() {
        return(
        <Container>
            <Breadcrumb>
                    <Breadcrumb.Item href="#/">All Products</Breadcrumb.Item>
                    <Breadcrumb.Item active>Shopping Cart</Breadcrumb.Item>
                </Breadcrumb>
            <Table striped bordered hover>
                <thead>
                    <tr>
                        <th>Image</th>
                        <th>ProductName</th>
                        <th>Price</th>
                        <th>Action</th>
                    </tr>
                </thead>
                <tbody>
                        {this.renderShoppingCartItems()}             
                </tbody>
            </Table>
        </Container>
        )
    }
}
