import firebase from 'firebase';
import React from 'react'

type OnAuthStateChange = (isLoading: boolean) => void;

export default class AuthService {
  firebaseConfig = {
    apiKey: "AIzaSyA8QwQuc5kHcPXv2CSwjph2C_pznIAZZ0Y",
    authDomain: "rafe-venturenix-project.firebaseapp.com",
    projectId: "rafe-venturenix-project",
    storageBucket: "rafe-venturenix-project.appspot.com",
    messagingSenderId: "214419668077",
    appId: "1:214419668077:web:be42c5b042ed852cff9364",
    measurementId: "G-1HF0PJ0NTK"
  };

  signedInEmail?: string | null;

  onAuthStateChange: OnAuthStateChange;

  constructor(onAuthStateChange: OnAuthStateChange) {
    this.onAuthStateChange = onAuthStateChange;
  }

  init() {
    if (!firebase.apps.length){
    firebase.initializeApp(this.firebaseConfig);
  } else{
    firebase.app();
  }
    //below kinda blackbox, for firebase received email,password and pass it as user in onAuthStateChange function
    firebase.auth().onAuthStateChanged((user) => {
      console.log("onAuthStateChanged: ", user)
      if (user) {
        //signed in
        this.signedInEmail = user?.email;
        this.onAuthStateChange(false);
      } else {
        //signed out
        this.signedInEmail = undefined;
        this.onAuthStateChange(false)
      }
    })
  }

  isSignedIn() {
    return this.signedInEmail != undefined && this.signedInEmail != null;
  }

  signInWithEmailPassword(email: string, password: string) {
    this.onAuthStateChange(true);

    firebase.auth().signOut()
      .then(() => {
        firebase.auth().signInWithEmailAndPassword(email, password)
          .catch((error) => {
            console.log(error);
            this.onAuthStateChange(false);
          });
      });
  }

  signInWithGoogle() {
    this.onAuthStateChange(true);

    firebase.auth().signOut()
      .then(() => {
        const googleAuthProvider = new firebase.auth.GoogleAuthProvider();
        firebase.auth().signInWithPopup(googleAuthProvider)
          .catch((error) => {
            console.log(error);
            this.onAuthStateChange(false);
          });
      });
  }

  signInWithFacebook() {
    this.onAuthStateChange(true);

    firebase.auth().signOut()
      .then(() => {
        const facebookAuthProvider = new firebase.auth.FacebookAuthProvider();
        firebase.auth().signInWithPopup(facebookAuthProvider)
          .catch((error) => {
            console.log(error);
            this.onAuthStateChange(false);
          });
      });
  }

  signOut() {
    this.onAuthStateChange(true);
    firebase.auth().signOut()
  }
}
