import React from 'react';
import Table from 'react-bootstrap/esm/Table';
import { ProductMap } from '../../../domain/dto/backendDos';

type Props = {
    displayItems: ProductMap
};
type State = {};

export default class ProductList extends React.Component<Props, State> {
    renderShoppingCartItems(){
        const cartItems: JSX.Element[]=[];

        if(!this.props.displayItems){
            return null;
        }

        for (let productId of Object.keys(this.props.displayItems)){
            //get the actual shopping cart item from this.props.displayItems by the key "productId"
            //Prefixed a "+" to productId to convert it from string to number
            const item = this.props.displayItems[+productId];
            cartItems.push(
                <tr>
                <td><img className="cartImg" src={item.imageUrl}/></td>
                <td>{item.name}</td>
                <td>{item.price}</td>
                <td>
                    <Button variant="danger" 
                    onClick={() => this.onClickRemoveItemButton(item.id)}
                >
                    <FontAwesomeIcon icon={faTrashAlt}/>Delete</Button></td>
            </tr>
            )
        }
        return cartItems
    };
    onClickRemoveItemButton(productId: number){
        // ... this state.shoppingCartItems means take everything
        //inside this.props.displayItems out and put them into the new object
        const shoppingCartItems = {...this.props.displayItems};
        
        delete shoppingCartItems[productId];

        this.setState({
            shoppingCartItems: shoppingCartItems
        });
    }
    render(){
        return(
            <Table striped bordered hover>
            <thead>
                <tr>
                    <th>Image</th>
                    <th>ProductName</th>
                    <th>Price</th>
                    <th>Action</th>
                </tr>
            </thead>
            <tbody>
                    {this.renderShoppingCartItems()}             
            </tbody>
        </Table>
        )
    }
}
