import { faTrashAlt } from '@fortawesome/free-solid-svg-icons';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import React from 'react';
import { Container } from 'react-bootstrap';
import Button from 'react-bootstrap/esm/Button';
import Table from 'react-bootstrap/esm/Table';
import { DisplayMap } from '../../../domain/dto/backendDos';
import ShoppingCartService from '../../../service/ShoppingCartService';
import Quantity from '../quantity';

type Props = {
    shouldShowRemoveButton: boolean,
    displayItems?: DisplayMap,
    onClickRemoveItemButton?: (productId: number) => void,
    onUpdateQuantity?: (productId: number, quantity: number) => void,
    shouldShowQuantityButton: boolean
};
type State = {};

export default class ProductList extends React.Component<Props, State> {
    renderListItems(){
        const cartItems: JSX.Element[]=[];

        if(!this.props.displayItems){
            return null;
        }

        for (let productId of Object.keys(this.props.displayItems)){
            //get the actual shopping cart item from this.props.displayItems by the key "productId"
            //Prefixed a "+" to productId to convert it from string to number
            const item = this.props.displayItems[+productId];
            //const product= this.props.shoppingCartService.getCart()[+productId]
            cartItems.push(
                <tr>
                <td><img className="cartImg" src={item.imageUrl}/></td>
                <td>{item.name}</td>
                <td>{item.price}</td>
                <Container>
                    {
                        (this.props.shouldShowQuantityButton)?
                        (<td><Quantity
                            productId={+productId}
                            quantity={item.quantity}
                            onUpdateQuantity={this.props.onUpdateQuantity!}
                            />
                        </td>) : null
                    }
                </Container>
                
                

                <td>{item.subtotal}</td>
                <Container>
                    {
                        (this.props.shouldShowRemoveButton) ? (
                            <td>    
                                <Button 
                                variant="danger" 
                                onClick={() => this.props.onClickRemoveItemButton!(item.id)}
                                >
                                <FontAwesomeIcon icon={faTrashAlt}/>Delete
                                </Button>
                            </td>
                        ) : null
                    }
                </Container>
                
               </tr>
            )
        }
        return cartItems
    }
 
    render() {
        return(
            <Table striped bordered hover>
            <thead>
                <tr>
                    <th>Image</th>
                    <th>Product Name</th>
                    <th>Unit Price</th>
                    <th>Quantity</th>
                    <th>Subtotal</th>
                    {
                        (this.props.shouldShowRemoveButton) ? (
                            <th>Action</th>
                        ) : null
                    }
                </tr>
            </thead>
            <tbody>
                    {this.renderListItems()}
            </tbody>
        </Table>
        );
    }
}
