import React from "react";
import { Breadcrumb, Container } from "react-bootstrap";
import Button from "react-bootstrap/esm/Button";
import { ProductItem } from "../../../../domain/dto/backendDos";
import BackendExtService from "../../../../extService/BackendExtService";
import "./style.css";

type Props = {};
type State = {
    productDetail?: ProductItem
};

export default class ProductDetailPage extends React.Component<Props, State> {
    state = {} as State;

    constructor(props: Props) {
        super(props);

        this.onLoadedProductDetail = this.onLoadedProductDetail.bind(this);
    }
    componentDidMount() {
        BackendExtService.getProductDetail(this.onLoadedProductDetail);
    }

    onLoadedProductDetail(data: ProductItem) {
        this.setState({productDetail: data});
    }

    render() {
        return (
            <Container id="productDetailPage">
                <Breadcrumb>
                    <Breadcrumb.Item href="#/">All Products</Breadcrumb.Item>
                    <Breadcrumb.Item active>Nitro Canon</Breadcrumb.Item>
                </Breadcrumb>
                <h1>Nitro Canon</h1>
                <img className="bannerImage" src="https://nitrosnowboards.com/sites/default/files/styles/variant_photos_protrait_no_crop_xl/public/2020-07/21-cannon-203.png.webp?itok=PtJl1ntI" />

                <h3>Description: </h3>
                <p>This is the snowboard </p>

                <Button variant="primary">Add to cart</Button>
            </Container>
        );
    }
}
