import React from "react";
import { Col, Container, Form } from "react-bootstrap";
import Breadcrumb from "react-bootstrap/esm/Breadcrumb";
import Table from "react-bootstrap/esm/Table";
import { RouteComponentProps, withRouter } from "react-router-dom";
import { DisplayMap, ProductItem, Transaction } from "../../../domain/dto/backendDos";
import BackendExtService from "../../../extService/BackendExtService";
import AuthService from "../../../service/AuthService";
import ProductList from "../../component/ProductList";
import "./style.css"

type RouterParamProps = {
    transactionId: string
}
type Props = RouteComponentProps<RouterParamProps> & {
    // shouldShowQuantityButton:boolean
};
type State = {
    
    transaction?: Transaction;

    creditCardNumber: string,
    expiryDate: string,
    cvv: string
};

class CheckoutPage extends React.Component<Props, State>{
    state = {
        creditCardNumber: "",
        expiryDate: "",
        cvv: ""
    } as State;    //Initial State

    constructor(props: Props) {
        super(props);

        this.onLoadedTransaction = this.onLoadedTransaction.bind(this);
        this.handleInputChange = this.handleInputChange.bind(this);
    }
    componentDidMount() {
        AuthService.getIdToken()
            .then((idtoken)=>{
                BackendExtService.getTransaction(idtoken,
                    +this.props.match.params.transactionId,
                    this.onLoadedTransaction)
            })
    }

    onLoadedTransaction(data: Transaction) {
        this.setState({
            transaction: data
        });
    }

    handleInputChange(event: React.ChangeEvent<HTMLInputElement>) {
        const target = event.target;
        const value = target.type === 'checkbox' ? target.checked : target.value;
        const name = target.name;

        //@ts-ignore
        this.setState({
            [name]: value
        });
    }

    renderProductList() {
        if (!this.state.transaction) {
            return null;
        }
        const items = this.state.transaction.items;
        const checkoutItems: DisplayMap = {};
        for (let item of items) {
            const details = item.details;
            checkoutItems[item.details.id] = {
                id: details.id,
                name: details.name,
                description: details.description,
                price: details.price,
                imageUrl: details.imageUrl,
                quantity: item.quantity,
                subtotal: item.subtotal
            }
        }

        return (
            <section>
                <ProductList
                    shouldShowRemoveButton={false}
                    displayItems={checkoutItems}
                    shouldShowQuantityButton={false}
                    
                />
                <h3>Total: ${this.state.transaction.total}</h3>
            </section>
        )
    }

    render() {
        return (
            <div className="checkout">
                <Container>
                    <div>
                        <Breadcrumb>
                            <Breadcrumb.Item href="#/">All Products</Breadcrumb.Item>
                            <Breadcrumb.Item href="#/cart">Shopping Cart</Breadcrumb.Item>
                            <Breadcrumb.Item active>Checkout</Breadcrumb.Item>
                        </Breadcrumb>

                        <h1>Checkout</h1>
                        {this.renderProductList()}
                    </div>
                    <Form>
                        <Form.Row>
                            <Form.Group as={Col} controlId="creditCardNumber">
                                <Form.Label>Credit Card #</Form.Label>
                                <Form.Control
                                    type="text"
                                    name="creditCardNumber"
                                    value={this.state.creditCardNumber}
                                    placeholder="Credit Card Number"
                                    onChange={this.handleInputChange}
                                />
                            </Form.Group>

                            <Form.Group as={Col} controlId="expiryDate">
                                <Form.Label>Expiry Date</Form.Label>
                                <Form.Control
                                    type="text"
                                    name="expiryDate"
                                    value={this.state.expiryDate}
                                    placeholder="Expiry Date"
                                    onChange={this.handleInputChange}
                                />
                            </Form.Group>

                            <Form.Group as={Col} controlId="cvv">
                                <Form.Label>CVV</Form.Label>
                                <Form.Control
                                    type="password"
                                    name="cvv"
                                    value={this.state.cvv}
                                    placeholder="CVV"
                                    onChange={this.handleInputChange}
                                />
                            </Form.Group>
                        </Form.Row>
                    </Form>
                </Container>
            </div>
        )
    }
}
export default withRouter(CheckoutPage);
