import { faTrashAlt } from '@fortawesome/free-solid-svg-icons';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import React from 'react';
import Button from 'react-bootstrap/esm/Button';
import Table from 'react-bootstrap/esm/Table';
import { ProductMap } from '../../../domain/dto/backendDos';

type Props = {
    shouldShowRemoveButton: boolean,
    displayItems?: ProductMap,
    onClickRemoveItemButton?: (productId: number) => void
};
type State = {};

export default class ProductList extends React.Component<Props, State> {
    renderListItems(){
        const cartItems: JSX.Element[]=[];

        if(!this.props.displayItems){
            return null;
        }

        for (let productId of Object.keys(this.props.displayItems)){
            //get the actual shopping cart item from this.props.displayItems by the key "productId"
            //Prefixed a "+" to productId to convert it from string to number
            const item = this.props.displayItems[+productId];
            cartItems.push(
                <tr>
                <td><img className="cartImg" src={item.imageUrl}/></td>
                <td>{item.name}</td>
                <td>{item.price}</td>
                {
                        (this.props.shouldShowRemoveButton) ? (
                            <td>    
                                <Button 
                                variant="danger" 
                                onClick={() => this.props.onClickRemoveItemButton!(item.id)}
                        >
                            <FontAwesomeIcon icon={faTrashAlt}/>Delete
                            </Button></td>
                        ) : null
                    }
               </tr>
            )
        }
        return cartItems
    }
 
    render() {
        return(
            <Table striped bordered hover>
            <thead>
                <tr>
                    <th>Image</th>
                    <th>ProductName</th>
                    <th>Price</th>
                    {
                        (this.props.shouldShowRemoveButton) ? (
                            <th>Action</th>
                        ) : null
                    }
                </tr>
            </thead>
            <tbody>
                    {this.renderListItems()}             
            </tbody>
        </Table>
        );
    }
}
