import React from 'react';
import "./style.css";

type Props = {
    productId: number
    quantity: number
    onUpdateQuantity: (productId: number, quantity: number) => void
};
type State= {  
    productId: number,  
    quantity: number
};

export default class Quantity extends React.Component <Props, State>{
    state={
        
    }as State

    constructor (props:Props){
        super(props);
        this.increment = this.increment.bind(this);
        this.decrement = this.decrement.bind(this);
        
    }

    increment() {
        this.props.onUpdateQuantity(this.props.productId, this.props.quantity + 1);
    }
      
    decrement() {

        this.props.onUpdateQuantity(this.props.productId, this.props.quantity>1? this.props.quantity-1: 1);
        

    }
    render(){


        return(
            <div>
                <div>
                    <button className="modifier --left " onClick={this.decrement}>
                    &mdash;
                    </button>
                    <input className="screen" type="text" value={this.props.quantity} />
                    <button className="modifier right " onClick={this.increment}>
                    &#xff0b;
                    </button>  
                </div>  
            </div>
            );
        }
    }
