import { faTrashAlt } from '@fortawesome/free-solid-svg-icons';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import React from 'react';
import { Breadcrumb, Button, Container, Table } from 'react-bootstrap';
import { DisplayMap, ProductMap, Transaction } from '../../../domain/dto/backendDos';
import { ShoppingCartItem } from '../../../domain/shoppingCartDos';
import BackendExtService from '../../../extService/BackendExtService';
import BackendAuthExtService from '../../../extService/BackendAuthExtService';
import ShoppingCartService from '../../../service/ShoppingCartService';
import ProductList from '../../component/ProductList';
import Quantity from '../../component/quantity';
import './style.css';
import AuthService from '../../../service/AuthService';
import { CreateTransactionRequestDto } from '../../../domain/dto/backendDtos';

type Props = {
    shoppingCartService: ShoppingCartService
};
type State = {
    shoppingCartItems?: DisplayMap
};

export default class ShoppingCartPage extends React.Component<Props, State> {
    state = {} as State; //initial state
    constructor(props: Props) {
        super(props);

        this.onLoadedShoppingCartItems = this.onLoadedShoppingCartItems.bind(this);
        this.onClickRemoveItemButton = this.onClickRemoveItemButton.bind(this);
        this.onClickCheckoutButton = this.onClickCheckoutButton.bind(this);
        this.onUpdateQuantity = this.onUpdateQuantity.bind(this);
    }
    componentDidMount() {
        const cart = this.props.shoppingCartService.getCart();
        const productIds = Object.keys(cart); /*find out all the keys in the object*/
        BackendExtService.getShoppingCartItems(productIds, this.onLoadedShoppingCartItems)
    }

    onLoadedShoppingCartItems(data: ProductMap) {
        const cart = this.props.shoppingCartService.getCart();

        const displayItems: DisplayMap = {};
        for (let productId of Object.keys(data)) {
            const value = data[+productId];
            displayItems[+productId] = {
                id: value.id,
                name: value.name,
                description: value.description,
                price: value.price,
                imageUrl: value.imageUrl,
                quantity: cart[+productId].productQuantity
            };
        }

        this.setState({
            shoppingCartItems: displayItems
        })
    }

    onClickCheckoutButton() {

        if(AuthService.isSignedIn === undefined){
            window.location.href = "#/login";
        }
        const checkoutItems: CreateTransactionRequestDto[] = [];
        for (let productId of Object.keys(this.state.shoppingCartItems!)) {
            const item = this.state.shoppingCartItems![+productId];
            checkoutItems.push({
                productId: +productId,
                quantity: item.quantity
            })
        }

        AuthService.getIdToken()
            .then((idToken) => {
                BackendExtService.checkout(idToken,checkoutItems,this.onCreatedTransaction);})
    }
    
    onCreatedTransaction(data: Transaction) {
        window.location.href = "#/checkout/" + data.transactionId;
    }

    onClickRemoveItemButton(productId: number) {
        this.props.shoppingCartService.removeFromCart(productId);
        // ... this state.shoppingCartItems means take everything
        //inside this.props.displayItems out and put them into the new object
        const shoppingCartItems = { ...this.state.shoppingCartItems };

        delete shoppingCartItems[productId];

        this.setState({
            shoppingCartItems: shoppingCartItems
        });
    }

    onUpdateQuantity(productId: number, quantity: number) {
        quantity = this.props.shoppingCartService.addToCart(productId,quantity);
        
        const shoppingCartItems = {...this.state.shoppingCartItems};

        shoppingCartItems[productId].quantity = quantity;
        this.setState({
            shoppingCartItems: shoppingCartItems
        });
    }

    renderCheckoutSection() {
        if (!this.state.shoppingCartItems) {
            return null;
        }
        let total = 0;
        // the productId is the 1,2 in the shoppingCartItem list and take the sum of the price
        for (let productId of Object.keys(this.state.shoppingCartItems)) {
            total += (this.state.shoppingCartItems[+productId].price*this.state.shoppingCartItems[+productId].quantity);
        }
        return (
            <section>
                { /*TODO*sub total*/}
                <hr /><h3>Total Price: {total}</h3>
                <br/>
                <Button variant="primary" onClick={this.onClickCheckoutButton}>Checkout</Button>
            </section>
        )
    }


    render() {
        return (
            <div className="shoppingCart">
                <Container>
                    <Breadcrumb>
                        <Breadcrumb.Item href="#/">All Products</Breadcrumb.Item>
                        <Breadcrumb.Item active>Shopping Cart</Breadcrumb.Item>
                    </Breadcrumb>

                    {/* TODO: Add back ProductList Component here*/}
                    <ProductList
                        shouldShowRemoveButton={true}
                        displayItems={this.state.shoppingCartItems}
                        onClickRemoveItemButton={this.onClickRemoveItemButton}
                        onUpdateQuantity={this.onUpdateQuantity}
                        shouldShowQuantityButton={true}
                    />

                    {this.renderCheckoutSection()}

                </Container>
            </div>
        )
    }
}

